import PropTypes from 'prop-types'
import Button  from './Button'


const Header = ({ title , onAdd, showAdd}) => {
  return (
        <header className='header'>
          <h1>{ title}</h1>
          <Button  color={showAdd ? 'red':'green'} text={showAdd ?'Close':'Add' } className='btn' onClick={onAdd}>Add</Button>
        </header>
       )
}
Header.defaultProps = {
    title:'Task Tracker',
}

Header.propTypes = {
    title:PropTypes.string.isRequired,
}


export default Header
