import { useState, useEffect } from 'react'
// import axios from "axios";
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom'
import Header from './components/Header'
import Footer from './components/Footer'
import Tasks from './components/Tasks'
import AddTask from './components/AddTask'
import About from './components/About'

// import About from './components/About'
const baseURL = "http://localhost:3001/";
const App = () => {
  const [showAddTask, setShowAddTask,data,setdata] = useState(false)
  const [tasks, setTasks] = useState([])

  useEffect(() => {
    getTasks();
  }, [])
      const getTasks = async () => {
        const res = await axios.get("http://localhost:3001/tasks");
           if (res.status===200)
        {
          setdata(res.data);
        }
      setTasks(res.data)
    }



  // Fetch Tasks
  // const fetchTasks = async (tasks) => {
  //   const res = await axios.get("http://localhost:3001/tasks");
  //   if (res.status===200)
  //   {
  //     setdata(res.data);
  //   }
  // }

  // Fetch Task
  const fetchTask = async (id) => {
    const res = await axios.get("http://localhost:3001/tasks/${id}")
    const data = await res.json()

    return data
  }

  //Add task

  const addTask = async (task) => {
    const res = await axios.get("http://localhost:3001/tasks/add", {
      method: 'POST',
      headers: {
        'Content-type': 'application/json',
      },
      body: JSON.stringify(task),
    })

    const data = await res.json()

    setTasks([...tasks, data])

    // const id = Math.floor(Math.random() * 10000) + 1
    // const newTask = { id, ...task }
    // setTasks([...tasks, newTask])
  }

  // Delete Task 
  const deleteTask = async (id) => {
    const res = await axios.get("http://localhost:3001/tasks/delete/${id}", {
      method: 'DELETE',
    })
    //We should control the response status to decide if we will change the state or not.
    res.status === 200
      ? setTasks(tasks.filter((task) => task.id !== id))
      : alert('Error Deleting This Task')
  }


  //Toggle Reminder
  const toggleReminder = async (id) => {
    debugger
    const taskToToggle = await fetchTask(id)
    const updTask = { ...taskToToggle, reminder: !taskToToggle.reminder }

    const res = await axios.get("http://localhost:3001/tasks/${id}", {
      method: 'PUT',
      headers: {
        'Content-type': 'application/json',
      },
      body: JSON.stringify(updTask),
    })

    const data = await res.json()
debugger
    setTasks(
      tasks.map((task) =>
        task.id === id ? { ...task, reminder: data.reminder } : task
      )
    )
  }


  return (
    <Router>
      <div>


      <div className='container'>


        <Header
          onAdd={() => setShowAddTask(!showAddTask)}
          showAdd={showAddTask}
        />
        <Routes>
          <Route
            path='/'
            element={
              <>
                {showAddTask && <AddTask onAdd={addTask} />}
                {tasks.length > 0 ? (
                  <Tasks
                    tasks={tasks}
                    onDelete={deleteTask}
                    onToggle={toggleReminder}
                  />
                ) : (
                  'No Tasks To Show'
                )}
              </>
            }
          />
          <Route path='/about' element={<About />} />
        </Routes>
        <Footer />
      </div>
      </div>
    </Router>
  )
}
export default App
